import React from 'react';
import './App.css'; // Importa o CSS para estilização

function App() {
  return (
    <div className="App">
      <header className="App-header">
        <h1>Minha Loja</h1>
      </header>
      <main>
        <section className="product-section">
          <h2>Produtos em Destaque</h2>
          <div className="products">
            {/* Exemplo de listagem de produtos */}
            <div className="product-item">
              <img src="link-para-imagem-do-produto" alt="Descrição do Produto" />
              <h3>Nome do Produto</h3>
              <p>R$ Preço</p>
              <button>Comprar</button>
            </div>
            {/* Repita a estrutura acima para mais produtos */}
          </div>
        </section>
      </main>
      <footer>
        <p>© 2024 Minha Loja. Todos os direitos reservados.</p>
      </footer>
    </div>
  );
}

export default App;
